import React, { useEffect, useState } from 'react';
import twitterLogo from './assets/twitter-logo.svg';
import {ethers} from "ethers";
const tld = '.sexy';
const CONTRACT_ADDRESS = 'YOUR_CONTRACT_ADDRESS_HERE';

export default function App() {

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);

	const [currentAccount, setCurrentAccount] = useState('');
	const [domain, setDomain] = useState('');
	const [record, setRecord] = useState('');
	const TWITTER_HANDLE = 'matt_brc';
	const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

	// Implement your connectWallet method here
	const connectWallet = async () => {
		try {
			const { ethereum } = window;

			if (!ethereum) {
				alert("Get MetaMask -> https://metamask.io/");
				return;
			}

		  	// Fancy method to request access to account.
			const accounts = await ethereum.request({ method: "eth_requestAccounts" });

		  	// Boom! This should print out public address once we authorize Metamask.
			console.log("Connected", accounts[0]);
			setCurrentAccount(accounts[0]);
		} catch (error) {
			console.log(error)
		}
	}

	// Gotta make sure this is async.
	const checkIfWalletIsConnected = async () => {
		const { ethereum } = window;
		if (!ethereum) {
			console.log("Make sure you have MetaMask!");
			return;
		} else {
			console.log("We have the ethereum object", ethereum);
		}

		// Check if we're authorized to access the user's wallet
		const accounts = await ethereum.request({ method: 'eth_accounts' });

		// Users can have multiple authorized accounts, we grab the first one if its there!
		if (accounts.length !== 0) {
			const account = accounts[0];
			console.log('Found an authorized account:', account);
			setCurrentAccount(account);
		} else {
			console.log('No authorized account found');
		}
	}

	// Render Methods
	const renderNotConnectedContainer = () => (
		<div className="connect-wallet-container">
			<img src="https://media.giphy.com/media/l4Jz3a8jO92crUlWM/giphy.gif" alt="salt gif" />
		  {/* Call the connectWallet function we just wrote when the button is clicked */}
			<button onClick={connectWallet} className="cta-button connect-wallet-button">
			Connect Wallet
			</button>
		</div>
	);

	// Form to enter domain name and data
	const renderInputForm = () =>{
		return (
			<div className="form-container">
				<div className="first-row">
					<input
						type="text"
						value={domain}
						placeholder='domain'
						onChange={e => setDomain(e.target.value)}
					/>
					<p className='tld'> {tld} </p>
				</div>

				<input
					type="text"
					value={record}
					placeholder='whats ur ninja power'
					onChange={e => setRecord(e.target.value)}
				/>

				<div className="button-container">
					<button className='cta-button mint-button' disabled={null} onClick={null}>
						Mint
					</button>  
					<button className='cta-button mint-button' disabled={null} onClick={null}>
						Set data
					</button>  
				</div>

			</div>
		);
	}

	  // This runs our function when the page loads.
	useEffect(() => {
		checkIfWalletIsConnected();
	}, [])

	const questions = [
		{
			questionText: 'A TV is made to ___ to you?',
			answerOptions: [
				{ answerText: 'talk', isCorrect: false },
				{ answerText: 'befriend', isCorrect: false },
				{ answerText: 'entertain', isCorrect: true },
				{ answerText: 'fall in love with', isCorrect: false },
			],
		},
		{
			questionText: 'What is the color yellow?',
			answerOptions: [
				{ answerText: 'Yellow', isCorrect: true },
				{ answerText: 'Duck', isCorrect: false },
				{ answerText: 'Apple', isCorrect: false },
				{ answerText: 'Blue', isCorrect: false },
			],
		},
		{
			questionText: 'What is 2 x 2 + 2?',
			answerOptions: [
				{ answerText: 'Elephant', isCorrect: false },
				{ answerText: '6', isCorrect: true },
				{ answerText: 'Amazon', isCorrect: false },
				{ answerText: '4', isCorrect: false },
			],
		},
		{
			questionText: 'Do you know where you live?',
			answerOptions: [
				{ answerText: 'Yes', isCorrect: true },
				{ answerText: 'No', isCorrect: false },
				{ answerText: 'I play football', isCorrect: false },
				{ answerText: 'Unicorns exist', isCorrect: false },
			],
		},
	];

	const handleAnswerButtonClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	}

	return (
		<div>
			<link href="https://fonts.googleapis.com/css2?family=Inter:wght@700&display=swap" rel="stylesheet"></link>
			<h1>midwit IQ Test</h1>
			<img className='header-image' src="https://i.imgur.com/iKlQSzg.jpeg" alt="midwit meme" />
			<div className='app'>
				{showScore ? (
					<div className='score-section'>You scored {(score / questions.length) * 100}%</div>
				) : (
					<>
						<div className='question-section'>
							<div className='question-count'>
								<span>Question {currentQuestion+ 1}</span>/{questions.length}
							</div>
							<div className='question-text'>{questions[currentQuestion].questionText}</div>
						</div>
						<div className='answer-section'>
							{questions[currentQuestion].answerOptions.map((answerOption, index) => (
								<button onClick={() => handleAnswerButtonClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
							))}
						</div>
					</>
				)}
			</div>

			{/* Hide the connect button if currentAccount isn't empty*/}
			{!currentAccount && renderNotConnectedContainer()}

				<link href="https://fonts.googleapis.com/css2?family=Inter&display=swap" rel="stylesheet"></link>
				<div className="footer-container">
					<img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
					<a
						className="footer-text"
						href={TWITTER_LINK}
						target="_blank"
						rel="noreferrer"
					>{`built by @${TWITTER_HANDLE}`}</a>
				</div>
		</div>
	);
}
