import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'A TV is made to ___ to you?',
			answerOptions: [
				{ answerText: 'talk', isCorrect: false },
				{ answerText: 'befriend', isCorrect: false },
				{ answerText: 'entertain', isCorrect: true },
				{ answerText: 'fall in love with', isCorrect: false },
			],
		},
		{
			questionText: 'What is the color yellow?',
			answerOptions: [
				{ answerText: 'Yellow', isCorrect: true },
				{ answerText: 'Duck', isCorrect: false },
				{ answerText: 'Apple', isCorrect: false },
				{ answerText: 'Blue', isCorrect: false },
			],
		},
		{
			questionText: 'What is 2 x 2 + 2?',
			answerOptions: [
				{ answerText: 'Elephant', isCorrect: false },
				{ answerText: '6', isCorrect: true },
				{ answerText: 'Amazon', isCorrect: false },
				{ answerText: '4', isCorrect: false },
			],
		},
		{
			questionText: 'Do you know where you live?',
			answerOptions: [
				{ answerText: 'Yes', isCorrect: true },
				{ answerText: 'No', isCorrect: false },
				{ answerText: 'I play football', isCorrect: false },
				{ answerText: 'Unicorns exist', isCorrect: false },
			],
		},
	];

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);

	const handleAnswerButtonClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	}

	return (
		<>
			<div id='navbar'>
			</div>
			<h1>midwit IQ Test</h1>
			<img className='header-image' src="https://i.imgur.com/iKlQSzg.jpeg" alt="midwit meme" />
			<div className='app'>
				{showScore ? (
					<div className='score-section'>You scored {(score / questions.length) * 100}%</div>
				) : (
					<>
						<div className='question-section'>
							<div className='question-count'>
								<span>Question {currentQuestion+ 1}</span>/{questions.length}
							</div>
							<div className='question-text'>{questions[currentQuestion].questionText}</div>
						</div>
						<div className='answer-section'>
							{questions[currentQuestion].answerOptions.map((answerOption, index) => (
								<button onClick={() => handleAnswerButtonClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
							))}
						</div>
					</>
				)}
			</div>
		</>
	);
}
